mapper.select:
SELECT n.*, (CASE WHEN tt.total IS NULL THEN 0 WHEN tt.total IS NOT NULL THEN tt.total END) AS comment_count 
FROM news n left JOIN 
(SELECT c.depend_id, COUNT(*) AS total FROM comment c INNER JOIN news s ON c.depend_id = s.id WHERE c.type = 1 GROUP BY depend_id)
tt on n.id = tt.depend_id

	
		n.user_id = #{userId}
	

ORDER BY create_time DESC LIMIT #{sinceCount}, #{pageSize}

mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO news(sub_news_id, user_id, content, create_time, type

	,link

)
VALUES
(#{subNewsId}, #{userId}, #{content}, #{createTime}, #{subType.type}

	,#{link}

)
